import { Component } from '@angular/core';

declare var webkitSpeechRecognition: any;

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.scss']
})
export class AppComponent {
    public speechRecognition: any;
    constructor() {
        if ('webkitSpeechRecognition' in window) {
            console.log("speech in window!")
            this.initSpeech();
        } else {
            console.log("speech NOT in window...")
        }
    }

    initSpeech() {
        this.speechRecognition = new webkitSpeechRecognition();
        console.log("init:",this.speechRecognition)

        this.speechRecognition.lang = 'en-US';
        this.speechRecognition.continuous = true;

        console.log("lang:",this.speechRecognition)

        this.startRecognition();
    }

    startRecognition() {
        this.speechRecognition.start();
        this.speechRecognition.addEventListener('result', (e:any) => {
            console.log("results?", e.results)
        });
    }

    // stop here ... 
}
